import { useState } from "react";
import { TabItem } from "./TabItem";
import './Tabs.css'

// export const Tabs = ({ list, activeTab, onTabSwitch}) => {
//     let active = activeTab === '' ? list[0] : activeTab;
   
//     return (
//         <div className="tab-cont">
//             <div className="tab">
//                 {list.map((item, index) => {
//                     return (
//                         <TabItem 
//                             title={item}
//                             key={index}
//                             index={index}
//                             active={active === item}
//                             setActive={onTabSwitch}
//                             />
//                     )
//                 })}
//             </div>
//         </div>
//     )
// }




function Tab(props) {
    return (
        <div className={`tabs__content`}>
            <h3>{props.tab.title}</h3>
            <p>{props.tab.text}</p>
        </div>
    );
}

function Navigation(props) {

    return (
        <ul className={`tabs__nav`}>
            {props.tabs.map((item) => (
                <li key={item.id} className={`tabs__item`}>
                    <button className={`tabs__button ${(props.activeTabId === item.id) ? 'active' : ''}`}
                            onClick={() => props.onNavClick(item.id)}>
                        {item.name}
                    </button>
                </li>
            ))}
        </ul>
    );
}

function Tabs(props) {
    const [activeTabId, setActiveTab] = React.useState(props.tabs[0].id);

    const activeTab = React.useMemo(() => (
        props.tabs.find((tab) => (
            tab.id === activeTabId
        ))
    ), [activeTabId, props.tabs]);

    return (

        <div className={`tabs`}>
            <Navigation tabs={props.tabs} onNavClick={setActiveTab}
                        activeTabId={activeTabId}/>
            <Tab tab={activeTab}/>
        </div>
    );
}

function App(props) {
    return (
        <div className='container'>
            <Tabs tabs={props.tabs}/>
        </div>
    );
}
