export const sliderSettings= {
    slidesPerView:1,
    spaceBetween:5,
    breakpoints:{
        480:{
            slidesPerView:1
        },
        600:{
            slidesPerView:2
        },
        650:{
            slidesPerView:2
        },
        700:{
            slidesPerView:3
        },
        750:{
            slidesPerView:3
        },
        1110:{
            slidesPerView:4
        }

    },
    autoplay: {
        delay: 1000, // Delay between slides in milliseconds
      }
}
