import './Tabs.css'



export const TabItem = ({ title, index, active, setActive}) => {
    const className = active ? 'border-b-yellow-400' : 'border-none text-slate-400';

    return (
        <div className='nav-item'>
            <button onClick={() => setActive(title)}>
                <span className={active}>
                    {title.toUpperCase()}
                </span>
            </button>
        </div> 
    )
}
