import React from 'react'
import {set, useForm} from 'react-hook-form'
import './AddressForm.css'
import TrendingFlatIcon from '@mui/icons-material/TrendingFlat';

import { useDispatch } from 'react-redux';
import { setAddress } from '../../Store/UserInfo/addressSlice';
const AddressForm = ({onTabSwitch}) => {
    const {register,handleSubmit} = useForm()
    const dispatch = useDispatch()
    const onSubmit =(data)=>{
        dispatch(setAddress(data))
        onTabSwitch('Payment')
    }

  return (
    <form className='address' onSubmit={handleSubmit(onSubmit)}>
         <p>Please select your favorite Web language:</p>
         <div>
          <input type="radio" id="take" name="address" value="takaway"
             checked/>
          <label for="takeaway">Take Away</label>
          </div>
          <div>
          <input type="radio" id="take" name="address" value="Home Delivery"
             checked/>
          <label for="HomeDelivery">Home Delivery</label>

          </div>

    </form>
  )
}

export default AddressForm



